import React, { Component } from "react";
import { Link } from "react-router-dom";
import { withRouter } from "react-router";
import "../../styles/Home.scss";
import axios from "axios";

const baseURL = "http://localhost:3000";

class Register extends Component {
  state = {
    user: "",
    pass: "",
    pass2: "",
  };

  handleOnChangeUsername = (event) => {
    this.setState({
      user: event.target.value,
    });
  };

  handleOnChangePassword = (event) => {
    this.setState({
      pass: event.target.value,
    });
  };

  handleOnChangeConfirmPassword = (event) => {
    this.setState({
      pass2: event.target.value,
    });
  };

  // handleRegister = (event) => {
  //   event.preventDefault();
  //   if (!this.state.user || !this.state.pass || !this.state.pass2) {
  //     alert("missing params");
  //     return;
  //   }

  //   this.setState({
  //     user: "",
  //     pass: "",
  //     pass2: "",
  //   });
  // };

  handleRegister = (e) => {
    if (!this.state.user || !this.state.pass || !this.state.pass2) {
      alert("missing params");
      return;
    }
    if (this.state.pass != this.state.pass2) {
      alert("pass 1 and pass 2 must gioosng nhau");
      return;
    }
    axios({
      method: "post",
      url: baseURL + "/register",
      data: {
        username: this.state.user,
        password: this.state.pass,
      },
    })
      .then((res) => {
        if (res.data.status == 1) {
          setTimeout(() => {
            this.props.history.push('/login');
          }, 2000);
        }
        else {
          alert("err Register");
        }
      })
      .catch((err) => {
        console.log(err);
      });
    e.preventDefault();
    console.log(this.state);
  };

  render() {
    return (
        <div className="background">
          <div className="container">
            <a href="/"></a><button> X </button>
            <div className="button-group">
              <div id="switch-register"></div>
              <Link to="/login">
                <button className="toggle-btn">Login</button>
              </Link>
              <button className="toggle-btn">Register</button>
            </div>
            <form>
              <div>
                <input
                  id="user"
                  type="text"
                  placeholder="Username"
                  value={this.state.user}
                  onChange={(event) => this.handleOnChangeUsername(event)}
                />
              </div>
              <div>
                <input
                  id="pass"
                  type="password"
                  placeholder="Password"
                  value={this.state.pass}
                  onChange={(event) => this.handleOnChangePassword(event)}
                />
              </div>
              <div>
                <input
                  id="pass2"
                  type="password"
                  placeholder="Confirm Password"
                  value={this.state.pass2}
                  onChange={(event) =>
                    this.handleOnChangeConfirmPassword(event)
                  }
                />
              </div>
              <div>
                <button
                  className="btn-register"
                  onClick={(e) => this.handleRegister(e)}
                >
                  Register
                </button>
              </div>
            </form>
          </div>
        </div>
    );
  }
}
export default withRouter(Register);
