  }

  render() {
    return (
      <div className="table-select-plant">
          <h1>Add Plant</h1>
          <label>Select plant</label>
        <div id="plants">
          {this.state.plants.map((item, index) => {
            return (
                <img src={item.name} alt="" onClick={() => this.handleSelectPLant(item.id)} />
            )
          })}
        </div>
      </div>
    );
  }
}
