import React, { Component } from "react";
import sapling from "../assets/images/sapling.svg";
import rootTree from "../assets/images/rootTree.png";
import smallPot from "../assets/images/smallPot.png";

export default class SelectPlant extends Component {
  state = {
    plantInInventory: [
      { id: 0, name: "sapling" },
      { id: 1, name: "root tree", img: rootTree },
    ],
    tools: [{ type: 0, name: "small pot", img: smallPot }],
  };
  render() {
    return (
      <div className="table-select-plant">
        <select>
          {this.state.plantInInventory.map((item, index) => {
            return <option>{item.name}</option>;
          })}
        </select>
        <select>
          {this.state.tools.map((item, index) => {
            return <option>{item.name}</option>;
          })}
        </select>
      </div>
    );
  }
}
