  render() {
    return (
      <div className="table-select-plant">
        <h1>Select a plant:</h1>
        <div id="plants">
          {this.state.plants.map((item, index) => {
            return (
              <img
                src={item.img}
                alt=""
                onClick={() => this.handleSelectPLant(item.id)}
                className="plant-img"
              />
            );
          })}
        </div>
      </div>
    );
  }
}
