import React, { Component } from 'react'
import '../styles/Land.scss'
import Plant from './Plant'

export default class Land extends Component {
    state = {
        numPlant: 0,
        arrPlants: [
            
        ]
    }
handleAddPlant = () => {
    this.state.arrPlants.push()

}

    render() {

        return (
          <div className="land">
            <div id="info-land">
              <button className="add-plant" onclick ={() => this.handleAddPlant()}> + </button>
            </div>
            <div id="body-land">
              {this.state.arrPlants}
            </div>
          </div>
        );
    }
}
